// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract AddressProvider {
    address public poaContract;
    address public bridgeState;
    address public owner;

    modifier onlyOwner(address sender) {
        require(
            owner == sender,
            "Only the owner of the contract can call this function"
        );
        _;
    }

    function setBridgeStateAddress(address _bridgeState)
        public
        onlyOwner(msg.sender)
    {
        bridgeState = _bridgeState;
    }

    function setPoaContractAddress(address _poaAddress)
        public
        onlyOwner(msg.sender)
    {
        poaContract = _poaAddress;
    }
}
