// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./libraries/BytesLib.sol";
import "./Struct.sol";
import "./interfaces/IBridgeState.sol";

contract POA {
    using BytesLib for bytes;

    address public _bridgeState;

    constructor(address _stateAddress) {
        _bridgeState = _stateAddress;
    }

    function parseAndVerifyVM(bytes calldata encodedPoa)
        public
        view
        returns (
            Struct.POAMessage memory poa,
            bool valid,
            string memory reason
        )
    {
        poa = parsePOA(encodedPoa);
        (valid, reason) = verifyPOA(poa);
    }

    function verifyPOA(Struct.POAMessage memory poa)
        public
        view
        returns (bool valid, string memory reason)
    {
        if (10 > poa.signatures.length) {
            return (false, "Don't engage, not enough consensus.");
        }

        // Verify signatures
        (bool signaturesValid, string memory invalidReason) = verifySignatures(
            poa.hash,
            poa.signatures
        );
        if (!signaturesValid) {
            return (false, invalidReason);
        }

        return (true, "");
    }

    function verifySignatures(
        bytes32 hash,
        Struct.Signature[] memory signatures
    ) public view returns (bool valid, string memory reason) {
        for (uint256 i = 0; i < signatures.length; i++) {
            Struct.Signature memory sig = signatures[i];
            address soldierAddress = IBridgeState(_bridgeState).getSoldier(
                sig.soldierNumber
            );
            if (ecrecover(hash, sig.v, sig.r, sig.s) != soldierAddress) {
                return (false, "VM signature invalid");
            }
        }
        return (true, "");
    }

    function parsePOA(bytes memory encodedPoa)
        public
        pure
        virtual
        returns (Struct.POAMessage memory poa)
    {
        uint256 index = 0;

        poa.payload = encodedPoa.slice(index, encodedPoa.length - index);

        // Parse Signatures
        uint256 signersLen = encodedPoa.toUint8(index);
        index += 1;
        poa.signatures = new Struct.Signature[](signersLen);
        for (uint256 i = 0; i < signersLen; i++) {
            poa.signatures[i].soldierNumber = encodedPoa.toUint8(index);
            index += 1;

            poa.signatures[i].r = encodedPoa.toBytes32(index);
            index += 32;
            poa.signatures[i].s = encodedPoa.toBytes32(index);
            index += 32;
            poa.signatures[i].v = encodedPoa.toUint8(index) + 27;
            index += 1;
        }

        // Hash the body
        bytes memory body = encodedPoa.slice(index, encodedPoa.length - index);
        poa.hash = keccak256(abi.encodePacked(keccak256(body)));
    }

    function parseTransfer(bytes memory encoded)
        public
        pure
        returns (Struct.Transfer memory transfer)
    {
        uint256 index = 0;

        transfer.amount = encoded.toUint256(index);
        index += 32;

        transfer.tokenAddress = encoded.toBytes32(index);
        index += 32;

        transfer.recipientAddress = encoded.toBytes32(index);
        index += 32;

        require(encoded.length == index, "invalid Transfer");
    }
}
