// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/IBridgeState.sol";
import "./SignatureValidator.sol";

contract Bridge is Pausable, SignatureValidator {
    // Mapping of  processed root chain transactions by root chain ID and txn hash. We follow official EVM Chain Id Standards.
    // e.g:
    // 1 is Ethereum Mainnet
    // 4 is Rinkeby
    mapping(uint32 => mapping(string => Transaction)) public transactions;

    // This variable ensures the bridge contract can check its own ID is equal to destinationChainId inside release tokens logic.
    uint32 public thisChainId; // Ethereum Mainnet Chain is Id'd 1;

    address public stateContract; // This address is basically of BridgeState which contains all the verifying methods and governance methods of the bridge.

    struct Transaction {
        bool processed;
        mapping(address => bool) signers;
    }

    constructor(uint32 chainId, address _stateAddress) {
        thisChainId = chainId;
        stateContract = _stateAddress;
    }

    // Events to monitor the bridge.
    // This event is emitted when user wants to lock existing ERC20 token to a foreign chain.
    // Tokens will be locked into the bridge smart contract.
    event Lock(
        address feeTokenAddr,
        uint256 feeAmount,
        address rootTokenAddr,
        uint256 rootAmount,
        string destinationTokenAddr,
        uint256 destinationAmount,
        string destinationAddr,
        uint32 destinationChainId,
        address fromAddr,
        uint32 rootChainId,
        uint8 signatureThreshold,
        uint256 timestamp
    );

    // This event is emitted when a user successfully bridged tokens on this chain.
    event BridgeSuccess(
        address toAddr,
        uint32 rootChainId,
        uint32 destinationChainId,
        address tokenAddr,
        string rootTxnId,
        uint256 amount,
        uint256 timestamp
    );

    /**
     * @dev This function is called to release tokens that were previously locked in this smart contract. This is the function called in the case of a bridge request with this chain as destination.
     */
    function releaseTokens(
        address tokenAddr,
        address toAddr,
        uint32 rootChainId,
        uint32 destinationChainId,
        string memory rootTxnId,
        uint256 amount,
        bytes[] memory signatures
    ) public whenNotPaused returns (bool) {
        require(
            IBridgeState(stateContract).isTokenValid(tokenAddr),
            "Trying to release non existent token."
        );
        // Require that you have enough signature to run the validation scheme.
        require(
            signatures.length >=
                IBridgeState(stateContract).getSignaturesThreshold(),
            "Length of signatures array is smaller than the required threshold"
        );
        // Check that this transaction has never been processed before.
        Transaction storage transaction = transactions[rootChainId][rootTxnId];
        require(
            transaction.processed != true,
            "Transaction ID already processed"
        );
        transaction.processed = true;

        // Reconstruct the hash that the soldiers signed using the function parameters.
        bytes32 reconstructedHash = reconstructMessageHash(
            toAddr,
            rootChainId,
            destinationChainId,
            tokenAddr,
            rootTxnId,
            amount
        );
        validateSignatures(
            stateContract,
            reconstructedHash,
            signatures,
            transaction.signers
        );

        // Emit success event.
        require(
            destinationChainId == thisChainId,
            "Destination Chain Id not equal to the Blockchain hosting this function call"
        );
        if (tokenAddr == address(0)) {
            payable(toAddr).transfer(amount);
        } else {
            // Transfer.
            IERC20(tokenAddr).transfer(toAddr, amount);
        }

        emit BridgeSuccess(
            toAddr,
            rootChainId,
            destinationChainId,
            tokenAddr,
            rootTxnId,
            amount,
            block.timestamp
        );
        return true;
    }

    /**
     * @dev This function lock tokens inside the bridge smart contract. This is used for bridge/swap requests originated on this chain and with destination chain represented by destinationChainId.
     */
    function lockTokens(
        address feeTokenAddr, // token to pay fee
        uint256 feeAmount, // fee amount to be locked
        address rootTokenAddr, // token at this network
        uint256 rootAmount, // amount to be locked at this network
        string memory destinationTokenAddr, // destination token address .. asa at algo, address at evm networks
        uint256 destinationAmount, // amount at the destination network .. usually the same for stable coins
        string memory destinationAddr, // the destination network address - memory = algo has different address than evm networks
        uint32 destinationChainId // destination chain
    ) public payable whenNotPaused returns (bool) {
        require(
            IBridgeState(stateContract).isTokenValid(rootTokenAddr),
            "Trying to bridge non existent token."
        );

        // Four cases:
        // 1 - Token used to pay fee is different from token to bridge && fee > 0
        if (
            rootTokenAddr != feeTokenAddr &&
            feeAmount > 0 &&
            feeTokenAddr != address(0)
        ) {
            require(
                IBridgeState(stateContract).isTokenValid(feeTokenAddr),
                "Trying to bridge non existent token."
            );
            IERC20(feeTokenAddr).transferFrom(
                msg.sender,
                address(this),
                feeAmount
            );
            IERC20(rootTokenAddr).transferFrom(
                msg.sender,
                address(this),
                rootAmount
            );
        } else if (rootTokenAddr == feeTokenAddr && feeAmount > 0) {
            // 2 - Token used to pay fee is the same as token to bridge && fee > 0
            IERC20(rootTokenAddr).transferFrom(
                msg.sender,
                address(this),
                rootAmount + feeAmount
            );
        } else if (
            msg.value == feeAmount &&
            feeTokenAddr == address(0) &&
            msg.value > 0
        ) {
            // 3 - Fee is paid in native currency (e.g. eth or matic)
            IERC20(rootTokenAddr).transferFrom(
                msg.sender,
                address(this),
                rootAmount
            );
        } else if (feeAmount == 0) {
            // 4 - Fee is 0. Will be paid on destination network or denied by soldier network
            IERC20(rootTokenAddr).transferFrom(
                msg.sender,
                address(this),
                rootAmount
            );
        } else {
            require(
                false,
                "Problem occurred while trying to select a transfer case"
            );
        }

        // Lock event accounts with rootChainId = thisChainId.
        emit Lock(
            feeTokenAddr,
            feeAmount,
            rootTokenAddr,
            rootAmount,
            destinationTokenAddr,
            destinationAmount,
            destinationAddr,
            destinationChainId,
            msg.sender,
            thisChainId,
            IBridgeState(stateContract).getSignaturesThreshold(),
            block.timestamp
        );
        return true;
    }

    /**
     * @dev This function lock native eth (or native chain token) inside the bridge smart contract.
     */
    function lockNativeCurrency(
        address feeTokenAddr, // token to pay fee
        uint256 feeAmount, // fee amount to be locked
        uint256 rootAmount, // amount to be locked at this network
        string memory destinationTokenAddr, // destination token address .. asa at algo, address at evm networks
        uint256 destinationAmount, // amount at the destination network .. usually the same for stable coins
        string memory destinationAddr, // the destination network address - memory = algo has different address than evm networks
        uint32 destinationChainId // destination chain
    ) public payable whenNotPaused returns (bool) {
        address fakeTokenAddrForNativeCurrency = address(0);
        // Three cases:
        if (msg.value == rootAmount + feeAmount) {
            // 1 - User pays in native currency the fee.
        } else if (msg.value == rootAmount && feeAmount > 0 && feeTokenAddr != address(0)) {
            // 2 - Token used to pay fee is a different token
            require(
                IBridgeState(stateContract).isTokenValid(feeTokenAddr),
                "Trying to pay fee with non existent token."
            );
            IERC20(feeTokenAddr).transferFrom(
                msg.sender,
                address(this),
                feeAmount
            );
        } else if (msg.value == rootAmount && feeAmount == 0) {
            // 3 - Fee is 0. Will be paid on destination network or denied by soldier network
        } else {
            require(false, "There was a problem in the fee ifs conditions");
        }

        // Lock event accounts with rootChainId = thisChainId.
        emit Lock(
            feeTokenAddr,
            feeAmount,
            fakeTokenAddrForNativeCurrency,
            rootAmount,
            destinationTokenAddr,
            destinationAmount,
            destinationAddr,
            destinationChainId,
            msg.sender,
            thisChainId,
            IBridgeState(stateContract).getSignaturesThreshold(),
            block.timestamp
        );
        return true;
    }

