    function deposit() public payable {}

    /**
     * @dev used to reconstruct a hash digest starting from some parameters.
     */
    function reconstructMessageHash(
        address toAddr,
        uint32 rootChainId,
        uint32 destinationChainId,
        address tokenAddr,
        string memory rootTxnId,
        uint256 amount
    ) public pure returns (bytes32) {
        return
            prefixed(
                keccak256(
                    abi.encodePacked(
                        toAddr,
                        rootChainId,
                        destinationChainId,
                        tokenAddr,
                        rootTxnId,
                        amount
                    )
                )
            );
    }
